const display = document.getElementById("display");

const produceChikuwa = document.getElementById("produce-chikuwa");
const myChikuwaDisplay = document.getElementById("my-chikuwa");

let myChikuwa = 0;

produceChikuwa.onclick = () => {
  myChikuwa += 1;
  if (myChikuwa % 15 == 0) {
    const image = createImage("isobe");
    display.append(image);
  } else if (myChikuwa % 5 == 0) {
    const image = createImage("chikuwa2");
    display.append(image);
  } else {
    const image = createImage("chikuwa1");
    display.append(image);
  }
  myChikuwaDisplay.textContent = myChikuwa;
};

const createImage = (chikuwa) => {
  let chikuwaImgSrc = "";
  switch (chikuwa) {
    case "chikuwa1":
      chikuwaImgSrc =
        "data:image/jpeg;base64,";
      break;
    case "chikuwa2":
      chikuwaImgSrc =
        "data:image/jpeg;base64,";
      break;
    case "isobe":
      chikuwaImgSrc =
        "data:image/jpeg;base64,";
      break;
    default:
      chikuwaImgSrc =
        "https://upload.wikimedia.org/wikipedia/commons/1/14/No_Image_Available.jpg";
  }
  const figure = document.createElement("figure");
  figure.classList.add("chikuwa__items-container__figure");
  const img = document.createElement("img");
  img.src = chikuwaImgSrc;
  img.classList.add("chikuwa__items-container__figure__img");
  figure.append(img);
  return figure;
};
